/* ---- Navbar ---- */
.navbar {
    background-color: #f36523;
    border-color: rgba(0,0,0,0.2);
    box-shadow: 0px 0px 10px rgba(0,0,0,0.6);
}

.navbar-default .navbar-toggle .icon-bar {
    background-color: white;
}

.navbar-form {
    border: 0px;
    -webkit-box-shadow: inset 0 0px 0 transparent;
    box-shadow: inset 0 0px 0 transparent;
}


.navbar-default .navbar-nav > li > a {
    font-weight: bold;
    color: white;
}

.navbar-default .navbar-brand {
    color: white;
}

.navbar-default .navbar-toggle {
    border-color: white;
}

    .navbar-default .navbar-toggle:hover, .navbar-default .navbar-toggle:focus {
        background-color: transparent;
    }

.navbar-default .navbar-nav > .open > a, .navbar-default .navbar-nav > .open > a:hover, .navbar-default .navbar-nav > .open > a:focus {
    color: white;
    background-color: transparent;
    border-radius: 5px;
}

.navbar-default .navbar-nav > li > a:hover,
.navbar-default .navbar-nav > li > a:focus {
    color: white;
    background-color: transparent;
}

@media (min-width:768px) {
    .navbar-default .navbar-nav > li > a:hover,
    .navbar-default .navbar-nav > li > a:focus {
        -webkit-box-shadow: 0px -4px 0px #414045 inset;
        -moz-box-shadow: 0px -4px 0px #414045 inset;
        box-shadow: 0px -4px 0px #414045 inset;
    }

    .navbar-form .form-group {
        display: inline-block;
        margin-bottom: 0;
        vertical-align: middle;
    }

    .navbar-form .form-control {
        display: inline-block;
        width: auto;
        vertical-align: middle;
    }

    .navbar-form .input-group {
        display: inline-table;
        vertical-align: middle;
    }

        .navbar-form .input-group .input-group-btn > button, .navbar-form .input-group .form-control > button {
            border-color: white;
        }

        .navbar-form .input-group .input-group-btn .btn.btn-default {
            background-color: white;
            outline: none;
            color: black;
        }

        .navbar-form .input-group > .form-control {
            width: 100%;
            background-color: #f36523;
            color: white;
        }

        .navbar-form .input-group > input {
            border-color: white;
        }

            .navbar-form .input-group > input:focus {
                border-color: rgba(255, 255, 255, 0.8);
                box-shadow: 0 1px 1px rgba(255, 255, 255, 0.075) inset, 0 0 8px rgba(255, 255, 255, 0.6);
                outline: 0 none;
            }

        .navbar-form .input-group > .form-control::-webkit-input-placeholder {
            color: rgba(255, 255, 255, 0.7);
        }

        .navbar-form .input-group > .form-control::-moz-placeholder {
            color: rgba(255, 255, 255, 0.7);
        }

        .navbar-form .input-group > .form-control::-ms-input-placeholder {
            color: rgba(255, 255, 255, 0.7);
        }

        .navbar-form .input-group > .form-control:-moz-placeholder {
            color: rgba(255, 255, 255, 0.7);
        }

        .navbar-form .input-group > .form-control:-ms-input-placeholder {
            color: rgba(255, 255, 255, 0.7);
        }

    .navbar-form .control-label {
        margin-bottom: 0;
        vertical-align: middle;
    }

    .navbar-form .radio, .navbar-form .checkbox {
        display: inline-block;
        margin-top: 0;
        margin-bottom: 0;
        vertical-align: middle;
    }

        .navbar-form .radio label, .navbar-form .checkbox label {
            padding-left: 0;
        }

        .navbar-form .radio input[type=radio], .navbar-form .checkbox input[type=checkbox] {
            position: relative;
            margin-left: 0;
        }

    .navbar-form .has-feedback .form-control-feedback {
        top: 0;
    }
}

/* ---- Unit ---- */
.thumbunit-center {
    object-fit: cover; /* Do not scale the image */
    object-position: center; /* Center the image within the element */
    width: 100%;
    height: 100%;
}

.thumbunit-container {
    overflow: hidden;
    width: 100%;
    height: 55%;
}

.grid_feunit {
    margin-top: 18px;
    overflow: hidden;
}

.caret {
    border-top-color: white !important;
    border-bottom-color: white !important;
}

/* ---- isotope ---- */

.grid {
    height: auto;
    margin: 18px auto;
    position: relative;
}

    /* clear fix */
    .grid:after {
        content: '';
        display: block;
        clear: both;
    }

/* ---- .element-item ---- */

.element-item {
    position: relative;
    float: left;
    width: 177px;
    height: 245px;
    margin: 5px;
    color: #262524;
    padding-top: 5px;
    overflow: hidden;
}

    .element-item > a:hover, .element-item:focus {
        outline: none;
        text-decoration: none;
    }

@media (max-width:991px) {
    .element-item {
        width: 170px;
        height: 253px;
    }
}

@media (max-width:406px) {
    .element-item {
        width: 240px;
        height: auto;
    }
}

.element-item .thumbunit-center {
    -webkit-filter: grayscale(100%);
    filter: grayscale(100%);
    -moz-transition: all 0.3s;
    -webkit-transition: all 0.3s;
    transition: all 0.3s;
}

.element-item:hover .thumbunit-center {
    -webkit-filter: grayscale(0%);
    filter: grayscale(0%);
    -moz-transform: rotate(5deg) scale(1.3);
    -webkit-transform: rotate(5deg) scale(1.3);
    transform: rotate(5deg) scale(1.3);
    cursor: pointer;
}

.element-item .name {
    position: absolute;
    left: 10px;
    top: 60px;
    text-transform: none;
    letter-spacing: 0;
    font-size: 12px;
    font-weight: normal;
}

.element-item .symbol {
    position: absolute;
    left: 10px;
    top: 0px;
    font-size: 42px;
    font-weight: bold;
    color: white;
}

.element-item .number {
    position: absolute;
    right: 8px;
    top: 5px;
}

.element-item .weight {
    position: absolute;
    left: 10px;
    top: 76px;
    font-size: 12px;
}


div.col-md-8 {
  --scrollbarBG: #414045;
  --thumbBG: #90A4AE;
}
div.grid_feunit::-webkit-scrollbar {
  width: 11px;
}
/* div.grid_feunit { */
  /* scrollbar-width: thin; */
  /* scrollbar-color: var(--thumbBG) var(--scrollbarBG); */
/* } */
div.grid_feunit::-webkit-scrollbar-track {
  background: var(--scrollbarBG);
}
div.grid_feunit::-webkit-scrollbar-thumb {
  background-color: var(--thumbBG) ;
  border-radius: 6px;
  border: 3px solid var(--scrollbarBG);
}
@media only screen and (min-width: 768px) {
	div.grid_feunit {
	  height: 80vh !important;
	  overflow-y: auto;
	  margin-right: -15px;
	}
}
